import digitaltransformation from "../assets/digitaltransformation.svg";
import consulting from "../assets/analytics.svg";
import anime from "animejs";


const testimonials = [
  {
    name: "Development Platforms",
    title: [
    <img src={nodejs} />, ' NodeJs,',  <img src={reactjs} />,' ReactJs,',  <img src={angularjs} />,' AngularJs' ,  
    <br />, <br />, 
    <img src={net} />, ' .Net,',<img src={php} />, ' Php,',<img src={python} />, ' Python' ,
     <br />,<br />,
    <img src={wordpress} />, ' WordPress,', <img src={magento} />, ' Magento,',<img src={opencart} />, ' Opencart' ,
     <br />,<br />, 
    <img src={ai} />, 'Ai,',  <img src={mongodb} />, 'MongoDB,', <img src={postgresql} />, ' PostgreSql' ,
  ],
    image: `${require("../assets/marketing.png")}`,
    quote:
      "Website Development"
  },
  {
    name: "Development Platforms",
    title: [
      <img src={android} />, ' Android Studio',
      <br />, <br />, 
      <img src={xcode} />,' Xcode',
       <br />,<br />,
       <img src={ionic} />,' Ionic',
       <br />,<br />,
       <img src={reactnative} />,' React Native'  
    ],
    image: `${require("../assets/marketing.png")}`,
    quote:
      "Mobile Development"
  },
  {
    name: "Development Platforms",
    title: [
      <img src={photoshop} />, ' Adobe Photoshop',
      <br />, <br />, 
      <img src={illustrator} />,' Adobe Illustrator',
       <br />,<br />,
       <img src={indesign} />,' Adobe Indesign',
       <br />,<br />,
       <img src={premier} />,' Adobe Premier',
       <br />,<br />,
       <img src={aftereffect} />,' Adobe After Effect'    
    ],
    // title: [<img src={reactjs} />, <br />, 'Logo and branding',  <br />, <img src={reactjs} />, <br />, 'Brand Identity', <br />, <img src={angularjs} /> , <br />, 'Concept Development', <br />, <img src={angularjs} />, <br />, 'Data Visualization', <br />, <img src={angularjs} />],
    image: `${require("../assets/marketing.png")}`,
    quote:
      "Digital Transformation"
  }
];

function Services() {


  class Card extends React.Component {
    constructor(props) {
      super(props);
    }

    render() {
      return (
        <div className="card-container">
          <input type="checkbox" id={this.props.id}/>

          <label className="card" htmlFor={this.props.id}>
            <div className="front">
              <h3 style={{textAlign:'center', fontWeight:'bold'}}>{this.props.title}</h3>
            </div>

            <div className="back">
              <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 841.9 595.3">
                <g fill="#61DAFB">
                    <path d="M666.3 296.5c0-32.5-40.7-63.3-103.1-82.4 14.4-63.6 8-114.2-20.2-130.4-6.5-3.8-14.1-5.6-22.4-5.6v22.3c4.6 0 8.3.9 11.4 2.6 13.6 7.8 19.5 37.5 14.9 75.7-1.1 9.4-2.9 19.3-5.1 29.4-19.6-4.8-41-8.5-63.5-10.9-13.5-18.5-27.5-35.3-41.6-50 32.6-30.3 63.2-46.9 84-46.9V78c-27.5 0-63.5 19.6-99.9 53.6-36.4-33.8-72.4-53.2-99.9-53.2v22.3c20.7 0 51.4 16.5 84 46.6-14 14.7-28 31.4-41.3 49.9-22.6 2.4-44 6.1-63.6 11-2.3-10-4-19.7-5.2-29-4.7-38.2 1.1-67.9 14.6-75.8 3-1.8 6.9-2.6 11.5-2.6V78.5c-8.4 0-16 1.8-22.6 5.6-28.1 16.2-34.4 66.7-19.9 130.1-62.2 19.2-102.7 49.9-102.7 82.3 0 32.5 40.7 63.3 103.1 82.4-14.4 63.6-8 114.2 20.2 130.4 6.5 3.8 14.1 5.6 22.5 5.6 27.5 0 63.5-19.6 99.9-53.6 36.4 33.8 72.4 53.2 99.9 53.2 8.4 0 16-1.8 22.6-5.6 28.1-16.2 34.4-66.7 19.9-130.1 62-19.1 102.5-49.9 102.5-82.3zm-130.2-66.7c-3.7 12.9-8.3 26.2-13.5 39.5-4.1-8-8.4-16-13.1-24-4.6-8-9.5-15.8-14.4-23.4 14.2 2.1 27.9 4.7 41 7.9zm-45.8 106.5c-7.8 13.5-15.8 26.3-24.1 38.2-14.9 1.3-30 2-45.2 2-15.1 0-30.2-.7-45-1.9-8.3-11.9-16.4-24.6-24.2-38-7.6-13.1-14.5-26.4-20.8-39.8 6.2-13.4 13.2-26.8 20.7-39.9 7.8-13.5 15.8-26.3 24.1-38.2 14.9-1.3 30-2 45.2-2 15.1 0 30.2.7 45 1.9 8.3 11.9 16.4 24.6 24.2 38 7.6 13.1 14.5 26.4 20.8 39.8-6.3 13.4-13.2 26.8-20.7 39.9zm32.3-13c5.4 13.4 10 26.8 13.8 39.8-13.1 3.2-26.9 5.9-41.2 8 4.9-7.7 9.8-15.6 14.4-23.7 4.6-8 8.9-16.1 13-24.1zM421.2 430c-9.3-9.6-18.6-20.3-27.8-32 9 .4 18.2.7 27.5.7 9.4 0 18.7-.2 27.8-.7-9 11.7-18.3 22.4-27.5 32zm-74.4-58.9c-14.2-2.1-27.9-4.7-41-7.9 3.7-12.9 8.3-26.2 13.5-39.5 4.1 8 8.4 16 13.1 24 4.7 8 9.5 15.8 14.4 23.4zM420.7 163c9.3 9.6 18.6 20.3 27.8 32-9-.4-18.2-.7-27.5-.7-9.4 0-18.7.2-27.8.7 9-11.7 18.3-22.4 27.5-32zm-74 58.9c-4.9 7.7-9.8 15.6-14.4 23.7-4.6 8-8.9 16-13 24-5.4-13.4-10-26.8-13.8-39.8 13.1-3.1 26.9-5.8 41.2-7.9zm-90.5 125.2c-35.4-15.1-58.3-34.9-58.3-50.6 0-15.7 22.9-35.6 58.3-50.6 8.6-3.7 18-7 27.7-10.1 5.7 19.6 13.2 40 22.5 60.9-9.2 20.8-16.6 41.1-22.2 60.6-9.9-3.1-19.3-6.5-28-10.2zM310 490c-13.6-7.8-19.5-37.5-