import React, { useRef } from "react";
import { useIntersection } from "react-use";
import gsap from "gsap";
import "../styles/pages/about.scss";
import { animateScroll as scroll} from 'react-scroll';

import arrow from '../assets/arrow-right.svg';


const App = () => {
  // Ref for our element
  const sectionRef = useRef(null);
  // All the ref to be observed
  const intersection = useIntersection(sectionRef, {
    root: null,
    rootMargin: "0px",
    threshold: 0.2
  });

  // Animation for fading in
  const fadeIn = element => {
    gsap.to(element, 1, {
      opacity: 1,
      y: -60,
      ease: "power4.out",
      stagger: {
        amount: 0.3
      }
    });
  };
  // Animation for fading out
  const fadeOut = element => {
    gsap.to(element, 1, {
      opacity: 0,
      y: -20,
      ease: "power4.out"
    });
  };

  // checking to see when the vieport is visible to the user
  intersection && intersection.intersectionRatio < 0.2
    ? fadeOut(".fadeIn")
    : fadeIn(".fadeIn");

    let app = useRef(null)
    let images = useRef(null)
    let content = useRef(null)
    let tl = new TimelineLite({ delay: .8});

    useEffect(() => {
      // Images Vars
      const girlImage = images.firstElementChild; // or children[0]
      const boyImage = images.lastElementChild;

      //content vars
      const headlineFirst = content.children[0].children[0];
      const headlineSecond = headlineFirst.nextSibling;
      const headlineThird = headlineSecond.nextSibling;
      const contentP = content.children[1];
      const contentButton = content.children[2];

      //Remove initial flash
      TweenMax.to(app, 0, {css: {visibility: 'visible'}})

      //Images Animation
      tl.from(girlImage, 1.2, {y: 1280, ease: Power3.easeOut},'Start')
      .from(girlImage.firstElementChild, 2, {scale: 1.6, ease: Power3.easeOut}, .2)
      .from(boyImage, 1.4, {y: 1280, ease: Power3.easeOut}, .2)
      .from(boyImage.firstElementChild, 2, {scale: 1.6, ease: Power3.easeOut}, .2)

      //Content Animation
      tl.staggerFrom([headlineFirst.children, headlineSecond.children, headlineThird.children ], 1, {
        y: 44,
        ease:Power3.easeOut,
        delay: .8
      }, .15, 'Start')
      .from(contentP, 1, {y: 20, opacity: 0, ease: Power3.easeOut}, 1.4)
      .from(contentButton, 1, {y: 20, opacity: 0, ease: Power3.easeOut}, 1.6)

    }, [tl])
  return (
  <>
     <div className='page'>
      <div className='container'>
        <div className='row'>
        <h3>GlobalxSolutions has always followed a "customer comes first" philosophy. Our governance is simple, flexible, and focused on providing end results to our clients. We value our customer relationships and take extra steps to ensure that our customers are always satisfied.</h3>
        <br></br>
        </div>
      </div>
     </div>
      <div className='container'>
        <div className='row'>
        <a onClick={() => scroll.scrollTo(950)}>Scroll Down</a>
        </div>
      </div>
      <div className="hero-images">
          <div ref={el => images = el}  className="hero-images-inner">
            <div className="hero-image first">
              <img src={arrow} alt="first" />
            </div>
            <div className="hero-image second">
              <img src={arrow} alt="second" />
            </div>
          </div>
        </div>
      <div className='sectionSecond'>
        <div ref={sectionRef} className='inner'>
          <h3 className='fadeIn'>We believe software is on the verge of disrupting medicine and becoming the accepted standard of care for treating diseases caused by behaviors.</h3>
          <p className='fadeIn'>
          The teamâ€™s technical excellence takes you halfway to success. At GlobalxSolutions, we understand that a solid grasp of your business domain and mature development approach are equally critical. Over the years, we have enriched and optimized our system development life cycle and software engineering skills to arrive at a predictable process that allows teams to minimize risks and cut costs. We place a high value on transparency and extensive communication to make sure the needs and expectations of every client are met 100%.
          </p>
          <div className='btn-row fadeIn'>
            <a href='/'>Services</a>
          </div>
        </div>
      </div>
    </>
  );
};

export default App;
