import React, { useEffect, useState } from "react";
import IntroOverlay from "../components/introOverlay";
import Banner from "../components/banner";
import Cases from "../components/cases";
import gsap from "gsap";

let tl = gsap.timeline();

const homeAnimation = completeAnimation => {
  tl.from(".line span", 1.8, {
    y: 100,
    ease: "power4.out",
    delay: 1,
    skewY: 7,
    stagger: {
      amount: 0.3
    }
  })
    .to(".overlay-top", 1.6, {
      height: 0,
      ease: "expo.inOut",
      stagger: 0.4
    })
    .to(".overlay-bottom", 1.6, {
      width: 0,
      ease: "expo.inOut",
      delay: -0.8,
      stagger: {
        amount: 0.4
      }
    })
    .to(".intro-overlay", 0, {
      css: { display: "none" }
    })
    .from(".case-image img", 1.6, {
      scale: 1.4,
      ease: "expo.inOut",
      delay: -2,
      stagger: {
        amount: 0.4
      },
      onComplete: completeAnimation
    });
};

const DarkModeToggle = () => {
  const [darkModeOn, setDarkMode] = useState(false);

  useEffect(() => {
    document.documentElement.setAttribute("data-dark-mode", darkModeOn);
  });

  return (
    <button
      className="dark-mode__btn"
      aria-pressed={darkModeOn}
      onClick={() => setDarkMode(!darkModeOn)}
    >
      <span className="dark-mode__content-wrap">
        <span className="dark-mode__icon" aria-hidden="true" />
        <span className="dark-mode__status" aria-hidden="true">{darkModeOn ? " on" : " off"}</span>
      </span>
    </button>
  );
};


const Home = ({ dimensions }) => {
  const [animationComplete, setAnimationComplete] = useState(false);

  const completeAnimation = () => {
    setAnimationComplete(true);
  };

  useEffect(() => {
    homeAnimation(completeAnimation);
  }, []);

  // useEffect(() => {
  //   let vh = dimensions.height * 0.01;
  //   document.documentElement.style.setProperty("--vh", `${vh}px`);
  // }, [dimensions.width]);

  return (
    <>
      {animationComplete === false ? <IntroOverlay /> : ""}
      <DarkModeToggle />
      <Banner />
      <Cases />
    </>
  );
};

export default Home;
