import React from "react";
import "../styles/pages/approach.scss";

const caseStudies = [
  {
    id: 1,
    title: "Web Development",
    subtitle: "Brand/Product sites, Consumer sites, E-commerce sites, Scalability ERP's, Product design and UI/UX, Business-tailored architecture, manual & automated testing",
    img: "web"
  },
  {
    id: 2,
    title: "Mobile Development",
    subtitle: "Tailor made applications, App that fulfils business goals, Native mobile app, Modern app designs, Hybrid apps using React Native, Long-term maintenance",
    img: "app"
  },
  {
    id: 3,
    title: "Digital Transformation",
    subtitle: "Digitizing core product or service, IT legacy problems and Rigid organizational culture, increase revenue, and innovate in a world that is “being eaten by the software",
    img: "marketing"
  }
];

const Approach = () => {
  return (
    <div>
    <section className='main'>
      <div className='container'>
        <div className='row'>
        <h2>
            <div className='line'>
              <span>Hello, We're GlobalxSolutions</span>
            </div>
            <div className='line'>
              <span>Software development & design teams</span>
            </div>
          </h2>
        </div>
      </div>
    </section>

<section className='cases'>
<div className='container-fluid'>
  <div className='row'>
    {caseStudies.map(caseItem => (
      <div className='case' key={caseItem.id}>
        <div className='case-details'>
          <span>{caseItem.subtitle}</span>
          <h2>{caseItem.title}</h2>
        </div>
        <div className='case-image'>
          <img
            src={require(`../assets/${caseItem.img}.png`)}
            alt={caseItem.title}
          />
        </div>
      </div>
    ))}
  </div>
</div>
</section>
</div>
  );
};

export default Approach;
