import React, { useRef } from "react";
import { useIntersection } from "react-use";
import gsap from "gsap";
import "../styles/pages/about.scss";
import { animateScroll as scroll} from 'react-scroll';

import arrow from '/assets/arrow-right.svg'


const App = () => {
  // Ref for our element
  const sectionRef = useRef(null);
  // All the ref to be observed
  const intersection = useIntersection(sectionRef, {
    root: null,
    rootMargin: "0px",
    threshold: 0.2
  });

  // Animation for fading in
  const fadeIn = element => {
    gsap.to(element, 1, {
      opacity: 1,
      y: -60,
      ease: "power4.out",
      stagger: {
        amount: 0.3
      }
    });
  };
  // Animation for fading out
  const fadeOut = element => {
    gsap.to(element, 1, {
      opacity: 0,
      y: -20,
      ease: "power4.out"
    });
  };

  // checking to see when the vieport is visible to the user
  intersection && intersection.intersectionRatio < 0.2
    ? fadeOut(".fadeIn")
    : fadeIn(".fadeIn");

  return (
    <>
     <div className='page'>
      <div className='container'>
        <div className='row'>
        <h3>GlobalxSolutions has always followed a "customer comes first" philosophy. Our governance is simple, flexible, and focused on providing end results to our clients. We value our customer relationships and take extra steps to ensure that our customers are always satisfied.</h3>
        </div>
        <button className="explore-button" onClick={() => scroll.scrollTo(550)}>Scroll Down
          <div className="arrow-icon">
            <a onClick={() => scroll.scrollTo(550)}>
                  {/* <img src={arrow} alt="row"/> */}
            </a>
          </div>
        </button>
        </div>
      </div>


      <div className='sectionSecond'>
        <div ref={sectionRef} className='inner'>
          <h3 className='fadeIn'>We believe software is on the verge of disrupting medicine and becoming the accepted standard of care for treating diseases caused by behaviors.</h3>
          <p className='fadeIn'>
          The teamâ€™s technical excellence takes you halfway to success. At GlobalxSolutions, we understand that a solid grasp of your business domain and mature development approach are equally critical. Over the years, we have enriched and optimized our system development life cycle and software engineering skills to arrive at a predictable process that allows teams to minimize risks and cut costs. We place a high value on transparency and extensive communication to make sure the needs and expectations of every client are met 100%.
          </p>
          <div className='btn-row fadeIn'>
            <a href='/'>Services</a>
          </div>
        </div>
      </div>
    </>
  );
};

export default App;
