import React from "react";
import anime from "animejs";
import "../styles/pages/approach.scss";

function runAnimtion() {
  anime({
    targets: '.counter-1',
    textContent: [0, 42],
    round: 1,
    easing: 'linear',
    duration: 1000
  });

  anime({
    targets: '.counter-2',
    textContent: [0, 77],
    round: 1,
    easing: 'linear',
    duration: 1000
  });

  anime({
    targets: '.counter-3',
    textContent: [0, 100],
    round: 1,
    easing: 'linear',
    duration: 1000
  });
}

const observer = new IntersectionObserver(
  entries => {
    entries.forEach(entry => {
      if (entry.intersectionRatio >= 0.7) {
        runAnimtion();
        observer.disconnect();
      }
    });
  },
  {
    threshold: 0.7
  }
);

observer.observe(document.querySelector('.target-section'));


const Approach = () => { 
  return (
    <div className='page'>
      <section>
      <h1>Scroll down...</h1>
    </section>
    <section class="target-section">
      <div class="container4">
        <div class="counter counter-1">0</div>
        <div class="counter counter-2">0</div>
        <div class="counter counter-3">0</div>
      </div>
    </section>
    <section>
      <h2>Another section</h2>
    </section>
    </div>
  );
};

export default Approach;
