class Careers extends Component {
  constructor() {
    super();
    this.state = { items: [], focused: null };
  }

  clickHandler = (id) => {
    this.setState({ focused: id });
  }

    getSelected(id) {
      switch(id)
        {
          case '1': return (<SelectedItem1 id={'1'} closeSelected={() => this.clickHandler(null)}/>)
          case '2': return (<SelectedItem2 id={'2'} closeSelected={() => this.clickHandler(null)}/>)
          case '3': return (<SelectedItem3 id={'3'} closeSelected={() => this.clickHandler(null)}/>)
          default: break;
        }
    }

  render() {
    const { focused } = this.state;

    return (
      <Flipper flipKey={focused} spring={{stiffness: 120,damping: 14}} >
        < div className={'portfolioContainer'}>
       {/* Grid  */}
          {!focused
            && (
              <div className={'grid'}>
                <Items openSelected={(id) => this.clickHandler(id)} />
              </div>
             )}
           {focused 
            && (this.getSelected(focused))
           }
        </div>
         </Flipper> 
    );
  }
}

class Items extends Component {
    constructor(props) {
    super(props);
    this.backgroundRef = createRef();
    this.stripeRef = createRef();
    this.imageRef = createRef();
    this.titleRef = createRef();

    this.hashRef = createRef(); //#2
    this.titleTextRef= createRef();
    this.subTitleTextRef= createRef();
    this.stripeRefs = []; //#3 
    this.boxRefs = []; //#3 
  }

   componentDidMount() {
     this.animateIn();
     this.animateIn2();
     this.animateIn3();
   }

   animateIn = () => {
     anime.timeline({
        easing: 'easeInOutSine',
      }).add({
        targets: [this.titleRef.current],
        opacity: [0, 1],
        translateY: [5,0],
        scaleY: [0, 1],
        duration: 500,
      }).add({
        targets: [this.stripeRef.current],
        opacity: [0, 1],
        scaleX: [0, 1],
       duration: 750,
      });
  }

  animateIn2 = () => {
    anime.timeline({
       easing: 'easeInOutSine',
       duration: 750,
        }).add({
            targets: [this.hashRef.current],
            opacity: [0, 1],
            scale: [1.0 , 0.75],
            rotateZ: '405deg',
       });
  }

    animateIn3 = () => {
    anime.timeline({
       easing: 'easeInOutSine',
       duration: 750,
      }).add({
        targets: [this.titleTextRef.current],
        opacity: [0, 1],
        translateY: [5,0],
        scaleY: [0, 1],
      }).add({
        targets: [this.subTitleTextRef.current],
        opacity: [0, 1],
        translateY: [5,0],
        scaleY: [0, 1],
      }).add({
            targets: [this.stripeRefs, this.boxRefs],
            opacity: [0, 1],
            scale: [0.0 , 1.0],
            delay: (d, i) => i * 50,
       });
  }

  addElement(ref,index,element) {
     if (element) {
       this[ref][index] = element;
     }
  }

  createItem1 = (type,hidden) => {
    return (
      <Fragment>
          <Flipped flipId={`one-background-${type}`} style={hidden ? {display:'none'} : {}}  >
            <div className={'background'} ref={this.backgroundRef}>
              <div className={'container'}  >
                <div className={`title fadeAnim`} ref={this.titleRef} >COMPANY NAME</div>
                <div className={`stripe fadeAnim`} ref={this.stripeRef} />
              </div>
              </div>
          </Flipped>
      </Fragment>
     )
  }

  createItem2 = (type,hidden) => {
      return (
        <Fragment>
            <Flipped flipId={`two-background-${type}`} style={hidden ? {display:'none'} : {}}  >
              <div className={'background'}>
                <img className={'hash fadeAnim'} src={hashIcon} ref={this.hashRef}/>
              </div>
            </Flipped>
        </Fragment>
     )
  }

  stripe() {
    console.log("Stripe");
    const stripe = ['red','white','blue'];

    return stripe.map(element =>{
      <div className={'stripe'} style={{background: element}}/>
    })
  }

  createItem3 = (type,hidden) => {
    const stripe = ['#D3D883','#D66166','#50C6E2','#E5C669','#D632AF','#D3D883','#D66166','#50C6E2','#E5C669','#D632AF'];
      return (
        <Fragment>
              <Flipped flipId={`three-background-${type}`} style={hidden ? {display:'none'} : {}}  >
                <div className={'background'}>
                  <div className={'title'}> 
                    <div className={'titleText'} ref={this.titleTextRef}> Vladislav Malcovick</div>
                    <div className={'underline'}/>
                    <div className={'subTitleText'} ref={this.subTitleTextRef}> Graphic and Interactive Design</div>
                  </div>
                  <div className={'stripeContainer'}>
                    {
                      stripe.map((element,index) =>{
                      return <div className={'stripe'} style={{'background-color': element}} 
                               ref={(el) => { this.addElement('stripeRefs',index, el); }}/>
                    })
                    }
                  </div>
                   <div className={'boxContainer'}> 
                      <div className={'box'} style={{'background-color': '#50C6E2'}}
                         ref={(el) => { this.addElement('boxRefs',0, el); }}><span>Web</span></div>
                      <div className={'box'} style={{'background-color': '#D632AF'}}
                         ref={(el) => { this.addElement('boxRefs',1, el); }}><span>Motion</span></div>
                      <div className={'box'} style={{'background-color': '#E5C669'}}
                         ref={(el) => { this.addElement('boxRefs',2, el); }}><span>Print</span></div>
                  </div>
                </div>
              </Flipped>
        </Fragment>
     )
  }

  render() {
    const { id, style } = this.props; 
    return (
      <Fragment>
          <div className={'one'} onClick={() => this.props.openSelected('1')} >
            {this.createItem1('horizontal')}
            {this.createItem1('vertical')}  
           </div>
          <div className={'two'} onClick={() => this.props.openSelected('2')} >
            {this.createItem2('vertical1')}
            {this.createItem2('vertical2')}
           </div>
           <div className={'three'} onClick={() => this.props.openSelected('3')} >
            {this.createItem3('horizontal')}
            {this.createItem3('vertical')}
           </div>
       </Fragment>
    );
  }
}

class SelectedItem1 extends Component {

