       </div> )
    }

    contentLeft(text1, text2, index1,index2){
      return (
        <Fragment>
           <div className={`stripeLeft fadeAnim`} ref={(el) => { this.addElement('leftStripes',index1, el); }} >
              <span className={'text '}> {text1} </span>
