           <div className={`stripeLeft2 fadeAnim`} ref={(el) => { this.addElement('leftStripes',index2, el); }} >
              <span className={'text'}> {text2} </span>
           </div>
        </Fragment>)
    }

   render() {
    const { closeSelected } = this.props;
       {/* Grid  */}
    return (
       <div className='selectedContainer' onClick={closeSelected}>
           <div className={'one-horizontal'} >
                <Flipped flipId={`one-background-horizontal`} onComplete={this.animateIn} >
                  <div className={'background'}>
                    {this.name(0,1)}
                    {this.contentLeft('12345 Road Street Suburb City 12345',null,0)}
                    {this.contentRight(0,0,1)}
