                </Flipped>
           </div>

           <div className={'one-vertical'}  >
                <Flipped flipId={`one-background-vertical`}>
                  <div className={'background'}>
                     {this.name(2,3)}
                     {this.contentLeft('12345 Road Street','Suburb City 12345',1,2)}
                     {this.contentRight(1,2,3)}
                  </div>
                </Flipped>
           </div>
       </div>
    );
  }
}

