class SelectedItem3 extends Component {

  constructor(props) {
    super(props);
    this.boxRefs = [];
    this.titleRefs = [];
    this.stripeRefs = [];
  }

  animateIn = () => {
     anime.timeline({
       easing: 'easeInOutSine',
       duration: 250,
     }).add({
        targets: [this.titleRefs, this.stripeRefs],
        opacity: [0, 1],
        translateY: [5,0],
        scaleY: [0, 1],
        delay: (d, i) => i * 50,
      }).add({
            targets: [this.boxRefs],
            opacity: [0, 1],
            scaleY: [0, 1],
            delay: (d, i) => i * 100,
       }) 
  };

  addElement(ref,index,element) {
     if (element) {
       this[ref][index] = element;
     }
  }

  stripes(offset) {
     const stripe = ['#D3D883','#D66166','#50C6E2','#E5C669','#D632AF','#D3D883','#D66166','#50C6E2','#E5C669','#D632AF'];
    return (<div className={'stripeContainer'}>
              {
                stripe.map((element,index) =>{
                return <div className={'stripe fadeAnim'} style={{'background-color': element}}
                         ref={(el) => { this.addElement('stripeRefs',index +offset, el); }}/>
                })
              }</div>)
  }

  box(icon,colour,text,index) {
    return (<div className={'box fadeAnim'}
              style={{'background-color': colour}} 
              ref={(el) => { this.addElement('boxRefs',index, el); }}>
              {icon}
              <span>{text}</span>
      </div>)
  }

  text(index1, index2, index3){
    return (<div className={'title'}> 
        <div className={'titleText fadeAnim'} ref={(el) => { this.addElement('titleRefs', index1, el); }}> Vladislav Malcovick</div>
        <div className={'underline fadeAnim'} ref={(el) => { this.addElement('titleRefs', index2, el); }}/>
        <div className={'subTitleText fadeAnim'} ref={(el) => { this.addElement('titleRefs', index3, el); }}> Graphic and Interactive Design</div>
     </div>)
  }

   render() {

    const { closeSelected } = this.props;
    return (
       <div className='selectedContainer' onClick={closeSelected}>
           <div className={'three-horizontal'}  >
                <Flipped flipId={`three-background-horizontal`} onComplete={this.animateIn}>
                  <div className={'background'} >
                     {this.text(0,1,2)}
                     <div className={'boxContainer'}> 
                       {this.box(brushIcon,'#50C6E2','Web . Motion . Print',0 )}
                       {this.box(phoneIcon,'#D632AF','+55 2186288735',1 )}
                       {this.box(emailIcon,'#E5C669','hello@malcovick.com',2 )}
                       {this.box(ieIcon,'#B8B8B8','www.malcovick.com',3 )}
                       {this.box(homeIcon,'#5C4188','1989 Albert Street, Blue South, Rio De Janiero,Brazil',4 )}
                    </div>
                  </div> 
                </Flipped>
           </div>

        <div className={'three-vertical'}  >
                <Flipped flipId={`three-background-vertical`}>
                  <div className={'background'}>
                    {this.stripes(0)}
                    {this.text(3,4,5)}
                    <div className={'boxContainer'}> 
                       {this.box(brushIcon,'#50C6E2','Web . Motion . Print',5 )}
                       {this.box(phoneIcon,'#D632AF','+55 2186288735',6)}
                       {this.box(emailIcon,'#E5C669','hello@malcovick.com',7 )}
                       {this.box(ieIcon,'#B8B8B8','www.malcovick.com',8 )}
                       {this.box(homeIcon,'#5C4188','1989 Albert Street, Blue South, Rio De Janiero,Brazil',9 )}
                    </div> 
                       <div className={'bottomStripes'}> {this.stripes(10)} </div>
                  </div>
                </Flipped>
           </div> {/* */}
       </div>
    );
  }
}


