import React, { useState, useRef, useEffect } from "react";
import { TweenLite, Power3 } from "gsap";

import "../styles/pages/services.scss";

import leftArrow from "../assets/arrow-left.svg";
import rightArrow from "../assets/arrow-right.svg";
import nodejs from "../assets/node-js.svg";
import reactjs from "../assets/react.svg";
import angularjs from "../assets/angular.svg";
import python from "../assets/python.svg";
import ai from "../assets/ai.svg";
import mongodb from "../assets/mongo_db.svg";
import postgresql from "../assets/postgre_sql.svg";
import wordpress from "../assets/wordpress.svg";
import magento from "../assets/magento.svg";
import opencart from "../assets/opencart.svg";
import net from "../assets/net.svg";
import php from "../assets/php.svg";
import android from "../assets/android.svg";
import xcode from "../assets/xcode.svg";
import ionic from "../assets/ionic.svg";
import reactnative from "../assets/react-1.svg";
import photoshop from "../assets/photoshop.svg";
import illustrator from "../assets/illustrator.svg";
import indesign from "../assets/indesign.svg";
import premier from "../assets/premier.svg";
import aftereffect from "../assets/aftereffect.svg";

const testimonials = [
  {
    name: "Development Platforms",
    title: [
    <img src={nodejs} />, ' NodeJs,',  <img src={reactjs} />,' ReactJs,',  <img src={angularjs} />,' AngularJs' ,  
    <br />, <br />, 
    <img src={net} />, ' .Net,',<img src={php} />, ' Php,',<img src={python} />, ' Python' ,
     <br />,<br />,
    <img src={wordpress} />, ' WordPress,', <img src={magento} />, ' Magento,',<img src={opencart} />, ' Opencart' ,
     <br />,<br />, 
    <img src={ai} />, 'Ai,',  <img src={mongodb} />, 'MongoDB,', <img src={postgresql} />, ' PostgreSql' ,
  ],
    image: `${require("../assets/yourspace-min.png")}`,
    quote:
      "Website Development"
  },
  {
    name: "Development Platforms",
    title: [
      <img src={android} />, ' Android Studio',
      <br />, <br />, 
      <img src={xcode} />,' Xcode',
       <br />,<br />,
       <img src={ionic} />,' Ionic',
       <br />,<br />,
       <img src={reactnative} />,' React Native'  
    ],
    image: `${require("../assets/curology-min.png")}`,
    quote:
      "Mobile Development"
  },
  {
    name: "Development Platforms",
    title: [
      <img src={photoshop} />, ' Adobe Photoshop',
      <br />, <br />, 
      <img src={illustrator} />,' Adobe Illustrator',
       <br />,<br />,
       <img src={indesign} />,' Adobe Indesign',
       <br />,<br />,
       <img src={premier} />,' Adobe Premier',
       <br />,<br />,
       <img src={aftereffect} />,' Adobe After Effect'    
    ],
    // title: [<img src={reactjs} />, <br />, 'Logo and branding',  <br />, <img src={reactjs} />, <br />, 'Brand Identity', <br />, <img src={angularjs} /> , <br />, 'Concept Development', <br />, <img src={angularjs} />, <br />, 'Data Visualization', <br />, <img src={angularjs} />],
    image: `${require("../assets/lumin-min.png")}`,
    quote:
      "Digital Transformation"
  }
];

function Services() {
  let imageList = useRef(null);
  let testimonialList = useRef(null);
  const imageWidth = 340;

  const [state, setState] = useState({
    isActive1: true,
    isActive2: false,
    isActive3: false
  });

  useEffect(() => {
    TweenLite.to(testimonialList.children[0], 0, {
      opacity: 1
    });
  }, []);

  //Image transition
  const slideLeft = (index, duration, multiplied = 1) => {
    TweenLite.to(imageList.children[index], duration, {
      x: -imageWidth * multiplied,
      ease: Power3.easeOut
    });
  };

  const slideRight = (index, duration, multiplied = 1) => {
    TweenLite.to(imageList.children[index], duration, {
      x: imageWidth * multiplied,
      ease: Power3.easeOut
    });
  };

  const scale = (index, duration) => {
    TweenLite.from(imageList.children[index], duration, {
      scale: 1.2,
      ease: Power3.easeOut
    });
  };

  //Content transition

  const fadeOut = (index, duration) => {
    TweenLite.to(testimonialList.children[index], duration, {
      opacity: 0
    });
  };

  const fadeIn = (index, duration) => {
    TweenLite.to(testimonialList.children[index], duration, {
      opacity: 1,
      delay: 1
    });
  };

  const nextSlide = () => {
    if (imageList.children[0].classList.contains("active")) {
      setState({ isActive1: false, isActive2: true });
      //Image transition
      slideLeft(0, 1);
      slideLeft(1, 1);
      scale(1, 1);
      slideLeft(2, 1);
      slideLeft(2, 0);
      fadeOut(0, 1);
      fadeIn(1, 1);
    } else if (imageList.children[1].classList.contains("active")) {
      setState({ isActive2: false, isActive3: true });
      //Image transition
      slideRight(0, 1);
      slideLeft(1, 1, 2);
      slideLeft(2, 1, 2);
      scale(2, 1);
      //content transition
      fadeOut(1, 1);
      fadeIn(2, 1);
    } else if (imageList.children[2].classList.contains("active")) {
      setState({ isActive1: true, isActive3: false });
      //Image transition
      slideLeft(2, 1, 3);
      slideLeft(0, 1, 0);
      slideLeft(1, 0, 0);
      scale(0, 1);
      //content transition
      fadeOut(2, 1);
      fadeIn(0, 1);
    }
  };

  const prevSlide = () => {
    if (imageList.children[0].classList.contains("active")) {
      setState({ isActive1: false, isActive3: true });
      //Image transition
      slideLeft(2, 0, 3);
      slideLeft(2, 1, 2);
      scale(2, 1);
      slideRight(0, 1);
      slideRight(1, 1);
      //content transtion
      fadeOut(0, 1);
      fadeIn(2, 1);
    } else if (imageList.children[1].classList.contains("active")) {
      setState({ isActive2: false, isActive1: true });
      //Image transition
      slideLeft(0, 0);
      slideRight(0, 1, 0);
      slideRight(1, 1, 0);
      slideRight(2, 1, 2);
      scale(0, 1);
      //content transtion
      fadeOut(1, 1);
      fadeIn(0, 1);
    } else if (imageList.children[2].classList.contains("active")) {
      setState({ isActive2: true, isActive3: false });
      slideLeft(2, 1);
      slideLeft(1, 0, 2);
      slideLeft(1, 1);
      scale(1, 1);
      //content transtion
      fadeOut(2, 1);
      fadeIn(1, 1);
    }
  };

  const caseStudies = [
    {
      id: 1,
      subtitle: "Web Development",
      title: [
        <img src={nodejs} />,  
       <br></br>,
       '* Frontend & Backend Development',
       <br></br>,
       '* Interface Design and Web Prototyping',
       <br></br>,
       '* UI/UX Design',
       <br></br>,
       '* Admin Panel/Administration',
      ]
    },
    {
      id: 2,
      subtitle: "Mobile Development",
      title: [
        <img src={nodejs} />,  
       <br></br>,
       '* Development for Android & iOS',
       <br></br>,
       '* UI/UX design',
       <br></br>,
       '* AR/VR',
       <br></br>,
       '* Integration with existing services',
      ]
    },
    {
      id: 3,
      subtitle: "Digital Transformation",
      title: [
        <img src={nodejs} />,  
       <br></br>,
       'Frontend & backend development',
       <br></br>,
       'Interface design and web prototyping',
       <br></br>,
       'UI/UX design',
       <br></br>,
       'Admin panel/administration',
      ]
    },
    {
      id: 4,
      subtitle: "Consulting & Analytics",
      title: [
        <img src={nodejs} />,  
       <br></br>,
       'Frontend & backend development',
       <br></br>,
       'Interface design and web prototyping',
       <br></br>,
       'UI/UX design',
       <br></br>,
       'Admin panel/administration',
      ]
    }
  ];

  return (
    <section className='cases'>
    <div className='container'>
      <div className="wrapper">
      <br></br>
      <br></br>
      <br></br>
      <br></br>
      <br></br>
      <br></br>
      <h2>
        Services
      </h2> 
      <h4>
        Over the many years of GlobalxSolutions existence,
        <br></br>
        the company has established itself as a reliable partner
        <br></br>
        that provides international quality 
        <br></br>
        services on time and on budget.
      </h4>
     <br></br>
     <div className='row'>
          {caseStudies.map(caseItem => (
              <div className='case-details'>
                <h2>{caseItem.subtitle}</h2>
                <h4>{caseItem.title}</h4>
            </div>
          ))}
    </div>

    <div className="testimonial-section">
      <div className="testimonial-container">
        <div onClick={prevSlide} className="arrows left">
          <span>
            <img src={leftArrow} alt="left arrow" />
          </span>
        </div>
        <div className="inner">
          <div className="t-image">
            <ul ref={el => (imageList = el)}>
              <li className={state.isActive1 ? "active" : ""}>
                <img src={testimonials[0].image} alt={testimonials[0].name} />
              </li>
              <li className={state.isActive2 ? "active" : ""}>
                <img src={testimonials[1].image} alt={testimonials[0].name} />
              </li>
              <li className={state.isActive3 ? "active" : ""}>
                <img src={testimonials[2].image} alt={testimonials[0].name} />
              </li>
            </ul>
          </div>
          <div className="t-content">
            <ul ref={el => (testimonialList = el)}>
              <li className={state.isActive1 ? "active" : ""}>
                <div className="content-inner">
                  <p className="quote">{testimonials[0].quote}</p>
                  <h3 className="name">{testimonials[0].name}</h3>
                  <h4 className="title">{testimonials[0].title}</h4>
                </div>
              </li>
              <li className={state.isActive2 ? "active" : ""}>
                <div className="content-inner">
                  <p className="quote">{testimonials[1].quote}</p>
                  <h3 className="name">{testimonials[1].name}</h3>
                  <h4 className="title">{testimonials[1].title}</h4>
                </div>
              </li>
              <li className={state.isActive3 ? "active" : ""}>
                <div className="content-inner">
                  <p className="quote">{testimonials[2].quote}</p>
                  <h3 className="name">{testimonials[2].name}</h3>
                  <h4 className="title">{testimonials[2].title}</h4>
                </div>
              </li>
            </ul>
          </div>
        </div>
        <div className="arrows right" onClick={nextSlide}>
          <img src={rightArrow} alt="right arrow" />
        </div>
      </div>
    </div>

    </div>   
    </div>   
    </section>
  );
}


export default Services;
