import React from "react";
import "../styles/pages/careers.scss";
import anime from "animejs";

$(function() {
  // #
  var beerCard = ".beer-card",
      $beerCard = $(beerCard),
      beerCard_inner = ".beer-card__inner",
      $beerCard_inner = $(beerCard_inner);

  // #
  $beerCard_inner.forEach(function(element, index) {
    // #
    element.animeJS = anime.timeline({
      autoplay: false
    });

    element.animeJS.add({
      targets: element,
      rotateY: [{ value: "180deg", duration: 100 }]
    });

    // #
    $(element).on("mouseenter", function() {
      beerCard_enter(false);
    });
    $(element).on("mouseleave", function() {
      beerCard_leave(false);
    });

    // #
    function beerCard_enter() {
      if (element.animeJS.reversed) element.animeJS.reverse();
      element.animeJS.play();
    }

    function beerCard_leave() {
      if (!element.animeJS.reversed) element.animeJS.reverse();
      element.animeJS.play();
    }
  });
});


 export default Careers;
