// App.js
import React from 'react';
import "../styles/pages/careers.scss";

	const user = {
	badge: 'https://www.codewars.com/users/kunal-mandalia/badges/micro',
	email: 'kunal.v.mandalia@gmail.com',
	template: 'https://codepen.io/kunalmandalia/pen/oZgjZy?editors=0110',
	first_name: 'Kunal',
	last_name: 'Mandalia',
	title: 'Founder at grraft.com',
	img_url: 'https://media.licdn.com/mpr/mpr/shrinknp_400_400/AAEAAQAAAAAAAAwbAAAAJDFhMzI4NTg1LTAxNzMtNDc0NS1iNTljLTBiNmI4NmMyNDhiZQ.jpg',
	social_links: [
	  {
		name: 'LinkedIn',
		icon: 'fa fa-linkedin',
		url: 'https://www.linkedin.com/in/kunal-mandalia-developer/'
	  },
	  {
		name: 'Github / Personal ',
		icon: 'fa fa-github',
		url: 'https://github.com/kunal-mandalia/'
	  },
	  {
		name: 'Github / Grraft',
		icon: 'fa fa-github',
		url: 'https://github.com/grraft/'
	  },
	  {
		name: 'Stack Overflow',
		icon: 'fa fa-code',
		url: 'http://stackoverflow.com/users/2438257/kunal',
	  },
	  {
		name: 'CodeWars',
		icon: 'fa fa-code',
		url: 'https://www.codewars.com/users/kunal-mandalia',
	  },
	  {
		name: 'Twitter',
		icon: 'fa fa-twitter',
		url: 'https://twitter.com/MandaliaKunal'
	  },
	],
	tags: ['React.js', 'Redux', 'MongoDB', 'Node.js', 'Express', 'Grraft.com'],
  }

  const credits = [
	{
	  name: 'Lionel Gustave (image)',
	  url: 'http://lionelgustave.com',
	},
	{
	  name: 'Yoann Boyer (image)',
	  url: 'https://unsplash.com/@yoannboyer'
	},
	{
	  name: 'Galymzhan Abdugalimov (image)',
	  url: 'https://unsplash.com/@naffiq',
	},
	{
	  name: 'FreeCodeCamp, for inspiring me',
	  url: 'https://www.freecodecamp.com/grraft'
	}
  ];

  const Credits = ({
	credits
  }) => {
	return credits.length > 0 ? (
	  <div className='credits'>
		With thanks to
		{credits.map((c, i) => {
		  return (
			<p key={i}>
			  <a href={c.url} target='_blank'>{c.name}</a>
			</p>
		  )
		})}
	  </div>
	) : ''
  }

  const ContactSection = ({
	user,
	credits
  }) => {
	return (
	  <div className='container'>
		<a name='contact'></a>
		<div className='contact-options'>

		<h1 className='section-heading'>Get in touch</h1>
		  <div className='row'>

			<div className='eight columns'>
			  <h6>
				<p>
				I'd love to hear from folks who'd make great mentors for me. Those with experience in startup growth, tech experts who've gotten their hands dirty with real apps and have core programming and design pattern principles nailed.
				</p>
				<p>
				  Interested in having a React/Node mentor made of synthetic tissue, not just silicon, in your life? I'm happy to help.
				</p>
				<p>
				  Finally if you're starting up and building out a tech team and are interested in having me on board then I'd be happy to consider
				   working as a Front End (React, Redux, ES5/6) / Full Stack (Node, Express, MongoDB) developer. The things I value include:
				</p>
				   <ul className='company-criteria'>
					 <li>Smart folks who've built commercially successful products</li>
					 <li>A tech stack aligned with my skillset</li>
					 <li>A culture of mentorship, at least within the tech team</li>
				   </ul>
			  </h6>
			</div>

			<div className='four columns'>
			  {user.social_links.map((s, i) => {
				return (
				  <a key={i} className='button button-primary stretch' href={s.url} target='_blank'>
					<i className={s.icon}></i> {s.name}
				  </a>
				);
			  })}
			  <a className='button stretch' href={`mailto:${user.email}?Subject=Hello`} target="_blank"><i className='fa fa-envelope-o'></i> Email</a>
			  <Credits credits={credits} />
			</div>
		  </div>
		</div>
	  </div>
	);
  }

  const App = () => {
	return(
		<div>
		<section className='contact-sections'>
		  <div className='contact-color'>
			<ContactSection user={user} credits={credits} />
		  </div>
		</section>
	  </div>
	)
	};


export default App;
