import React, { PureComponent } from "react";
import "../styles/pages/audit.scss";
import anime from "animejs";
import axios from 'axios';
import jsPDF from 'jspdf';

class CaseStudies extends React.Component {
    constructor(){
        super()
        this.state = {
        isSending: false,
        anim: false,
        name: '',
        company: '',
        email: '',
        phonenumber: '',
        message: ''
      }
      this.handleChange = this.handleChange.bind(this)
      this.handleSubmit = this.handleSubmit.bind(this)
    }
      handleChange = e => {
        this.setState({[e.target.name]: e.target.value })
      }
      validateForm = toggle => {
        const { anim, isSending } = this.state;
        if (anim === false && isSending === false) {
          console.log('hi')
          toggle
            ? anime({
                targets: ["#rectwrap rect"],
                strokeDashoffset: [anime.setDashoffset, 0],
            opacity: [{
              value:1,
              duration: 50
            }],
                easing: "easeOutQuad",
                duration: 650
              })

            : anime({
                targets: ["#rectwrap rect"],
                strokeDashoffset: [0, anime.setDashoffset],
                easing: "easeOutQuad",
                duration: 700
              });
        }
      };
      timeline = () => {
        const moo = () => {
          this.setState({ anim: false });
        };
        let tl = anime.timeline({
          easing: "easeInOutSine",
          changeComplete: function() {
            moo();
          }
        });
        tl.add({
          targets: ".inp",
          translateX: "100%",
          opacity: [1, 0],
          easing: "easeInOutSine",
          duration: 350,
          delay: (el, i) => 150 * i
        })
          .add({
            targets: "#submit",
            outlineWidth: "0px",
            duration: 0,
            easing: "easeInOutSine"
          })
          .add({
            targets: ["#rectwrap rect"],
            opacity: 0,
            easing: "easeOutQuad",
            duration: 100
          })
          .add({
            targets: ".siz",
            translateY: "60px",
            duration: 250
          })
          .add({
            targets: ".siz",
            translateY: "-600px",
          easing: "easeInQuad",
            duration: 500
          })
          .add({
            targets: ".siz",
            opacity: [1, 0],
            duration: 200
          });
      };
      reverseAnim = () => {
        const moo = () => {
          this.setState({ anim: false });
        };
        this.setState(
          {
            isSending: false,
            anim: true,
          },
          () => {
            let tl = anime.timeline({
              easing: "easeInOutSine",
              changeComplete: function() {
                moo();
              }
            });
            tl.add({
              targets: ".inp",
              translateX: "0%",
              opacity: [0, 1],
              easing: "easeInOutSine",
              duration: 350,
              delay: (el, i) => 150 * i
            })
              .add({
                targets: ".siz",
                translateY: "0%",
                translateX: "0%",
                duration: 0
              })
              .add({
                targets: "#submit",
                outlineWidth: "1px",
                duration: 300,
                easing: "easeInOutSine"
              })
              .add({
                targets: ".siz",
                opacity: [0, 1],
                duration: 300
              })
            .add({
              targets: ["#rectwrap rect"],
              opacity: [0],
              strokeDashoffset: [0],
              duration: 100
            })
          }
        );
      };
      handleSubmit = e => {
      e.preventDefault()

      const { name, company, email, phonenumber, message } = this.state

      const form =  axios.post('/api/forms', {
        name,
        company,
        email,
        phonenumber,
        message
      })

    this.setState({ anim: true, isSending: true }, () => {
    });
    this.timeline();

    document.getElementById("contactform").reset();
    }
