    jsPDFGenerator = () => {
            // new Document in jspdf
            var doc = new jsPDF();

            var imgData = "data:image/png;base64,";
            // some text of pdf
            // doc.text(20,20,'This is demo text')

            doc.addImage(imgData, 'PDF', 0,0,300,300)
            // set font 
            // doc.setFont('poppin');

            // font type
            // doc.setFontType('normal')

            // doc.text(20,30, 'This is text with font')

             // save
             doc.save('generated.pdf');

    }
    render() {
      const { isSending, anim } = this.state;
      if (anim === false && isSending === false) {
        console.log("Message Send Successfully")
      }
      return (
          <div>


            <div className='page'>
            <section className="content">
      <img className="logo" src="http://blog.addthiscdn.com/wp-content/uploads/2014/11/addthis-react-flux-javascript-scaling.png" alt="React JS" />
      <div className="separator"></div>
      <ul className="list">
        <li className="item">
          <div>
            <p className="item-title">Declarative</p>
            <p className="item-description">
              React makes it painless to create interactive UIs. Design simple views for each state in your application, and React will efficiently update and render just the right components when your data changes. Declarative views make your code more predictable and easier to debug.
            </p>
          </div>
        </li>
        <li className="item">
          <div>
            <p className="item-title">Component-Based</p>
            <p className="item-description">
              Build encapsulated components that manage their own state, then compose them to make complex UIs. Since component logic is written in JavaScript instead of templates, you can easily pass rich data through your app and keep state out of the DOM.
            </p>
          </div>
        </li>
        <li className="item">
          <div>
            <p className="item-title">Learn Once, Write Anywhere</p>
            <p className="item-description">
              We don't make assumptions about the rest of your technology stack, so you can develop new features in React without rewriting existing code. React can also render on the server using Node and power mobile apps using <a href="https://facebook.github.io/react-native/" target="_blank">React Native</a>.
            </p>
          </div>
        </li>
        <div className="separator"></div>
      </ul>
    </section>

                   </div>
         </div>
      );
    }
  }

export default CaseStudies;
