import React, { useRef } from "react";
import { useIntersection } from "react-use";
import gsap from "gsap";
import "../styles/pages/about.scss";
import anime from "animejs";

const App = () => {
  // Ref for our element
  const sectionRef = useRef(null);
  // All the ref to be observed
  const intersection = useIntersection(sectionRef, {
    root: null,
    rootMargin: "0px",
    threshold: 0.2
  });

  // Animation for fading in
  const fadeIn = element => {
    gsap.to(element, 1, {
      opacity: 1,
      y: -60,
      ease: "power4.out",
      stagger: {
        amount: 0.3
      }
    });
  };
  // Animation for fading out
  const fadeOut = element => {
    gsap.to(element, 1, {
      opacity: 0,
      y: -20,
      ease: "power4.out"
    });
  };

  // checking to see when the vieport is visible to the user
  intersection && intersection.intersectionRatio < 0.2
    ? fadeOut(".fadeIn")
    : fadeIn(".fadeIn");
     return (
    <> 
      <section className="content">
      <ul className="list">
        <li className="item">
          <div>
            <p className="item-title">Declarative</p>
            <p className="item-description">
              React makes it painless to create interactive UIs. Design simple views for each state in your application, and React will efficiently update and render just the right components when your data changes. Declarative views make your code more predictable and easier to debug.
            </p>
          </div>
        </li>
        <li className="item">
          <div>
            <p className="item-title">Component-Based</p>
            <p className="item-description">
              Build encapsulated components that manage their own state, then compose them to make complex UIs. Since component logic is written in JavaScript instead of templates, you can easily pass rich data through your app and keep state out of the DOM.
            </p>
          </div>
        </li>
        <li className="item">
          <div>
            <p className="item-title">Learn Once, Write Anywhere</p>
            <p className="item-description">
              We don't make assumptions about the rest of your technology stack, so you can develop new features in React without rewriting existing code. React can also render on the server using Node and power mobile apps using <a href="https://facebook.github.io/react-native/" target="_blank">React Native</a>.
            </p>
          </div>
        </li>
        <div className="separator"></div>
      </ul>

      <div className='sectionSecond'>
        <div ref={sectionRef} className='inner'>
          <h3 className='fadeIn'>We certainly don’t do ordinary. We take you on a journey to understand one-another... to realise the potential of your business and what magic we can make together.</h3>
          <p className='fadeIn'>
          The team’s technical excellence takes you halfway to success. At GlobalxSolutions, we understand that a solid grasp of your business domain and mature development approach are equally critical. Over the years, we have enriched and optimized our system development life cycle and software engineering skills to arrive at a predictable process that allows teams to minimize risks and cut costs. We place a high value on transparency and extensive communication to make sure the needs and expectations of every client are met 100%.
          </p>
          <h3 className='fadeIn'>What Makes Us Special?</h3>
          <p className='fadeIn'>
          It's simple: our remarkable team and our success-driven process. Our process is designed for your success. It's desgined for successful collaboration, transparency and efficiency.
          </p>
          <div className='btn-row fadeIn'>
            <a href='approach'>Work with us</a>
          </div>
        </div>
      </div>
      </section>

    </>
  );
};

export default App;
