// App.js
import React from 'react';

function getGlitchTimeline(el) {
  const isSafari =
    /constructor/i.test(window.HTMLElement) ||
    (function(p) {
      return p.toString() === '[object SafariRemoteNotification]';
    })(!window['safari'] || safari.pushNotification) ||
    /^((?!chrome|android).)*safari/i.test(navigator.userAgent);

  const turbVal = { val: 0.000001 };
  const turb = document.querySelectorAll('#filter-glitch feTurbulence')[0];
  const disp = document.querySelectorAll(
    '#filter-glitch feDisplacementMap'
  )[0];
  const Tl = new TimelineLite({
    paused: true,
    onUpdate() {
      turb.setAttribute('baseFrequency', '0.00001 ' + turbVal.val); // Firefox bug is value is 0
      if (isSafari) disp.setAttribute('in2', 'warp');
    },
    onStart() {
      el.style.filter = 'url(#filter-glitch)';
    },
    onComplete() {
      el.style.filter = 'none';
    },
  });

  Tl.to(turbVal, 0.1, { val: 0.8 });
  return Tl;
}

function glitchInit() {
  const glitched = document.querySelectorAll('.glitched');
  const glitchedRandomInterval = document.querySelectorAll(
    '.js-random-interval.glitched'
  );

  glitched.forEach(el => {
    const Tl = getGlitchTimeline(el);
    el.addEventListener('mouseover', () => Tl.restart());
  });

  glitchedRandomInterval.forEach(el => {
    const Tl = getGlitchTimeline(el);
    setInterval(() => Tl.restart(), 2000 + Math.random() * 5000);
  });
}

const nightMode = (on) => {
  const body = document.querySelector('body');
  on 
    ? body.classList.add('night') 
    : body.classList.remove('night');
}

const Index = () => (
  <div>
    <Page>
      <Toggle onToggle={on => nightMode(on)} />
      <Header />
      <Intro />
      <Availability />
      <GlitchEffect />
    </Page>
  </div>
);

const Page = ({ children }) => (
  <div className="page">
    {children}
    <style jsx>
      {`
        .page {
          max-width: 500px;
          margin-left: auto;
          margin-right: auto;
        }
      `}
    </style>
  </div>
);

class Toggle extends React.Component {
  static defaultProps = {onToggle: () => {}}
  state = {on: false}
  timeline = new TimelineMax();  
  toggle = () => {
    this.setState(
      ({on}) => ({on: !on}),
      () => {
        this.props.onToggle(this.state.on)
      },
    );
    this.timeline.reversed(!this.timeline.reversed());
  }
  componentDidMount() {
    this.timeline
      .to(document.querySelector('#pole'), 0.2, {y: '+=35', ease: Power1.easeInOut}, 0)
      .to(document.querySelector('#darkshadow'), 0.2, {y: '+=3', ease: Power1.easeInOut}, 0)
      .to(document.querySelector('#lightshadow'), 0.2, {y: '-=3', ease: Power1.easeInOut}, 0)
      .reverse();
  }
  render() {
    const {on} = this.state;
    return (
      <Switch on={on} onClick={this.toggle} />
    )
  }
}

const Switch = ({on, ...props}) => (
  <div className="switch-container">
  <svg id="switch" viewBox="0 0 88 148" xmlns="http://www.w3.org/2000/svg" preserveAspectRatio="xMidYMid slice" {...props}>
    <g fill="none" fillRule="evenodd">
      <path stroke="#BE2446" strokeWidth="6" d="M0 0h88v148H0z"/>
      <circle fill="#BE2446" fillRule="nonzero" cx="44" cy="20" r="4"/>
      <path id="lightshadow" fillOpacity=".2" fill="#BE2446" fillRule="nonzero" d="M30 72h28v9H30z"/>
      <path id="darkshadow" fillOpacity=".24999999" fill="#BE2446" d="M30 72h28v3H30z"/>
      <path stroke="#BE2446" strokeWidth="3" d="M29 43h30v64H29z" />
      <path id="pole" stroke="#BE2446" strokeWidth="6" d="M31 46h26v23H31z" />
      <circle fill="#BE2446" fillRule="nonzero" cx="44" cy="128" r="4"/>
    </g>
  </svg>
    <style jsx>{`
       svg {
         height: 48px;
       }
       #switch {
         cursor: pointer;
       }
       .switch-container {
         text-align: right;
         margin-top: 10px;
       }
    `}
    </style>
  </div>
);

const Header = () => (
  <h1>
    <strong className="js-random-interval glitched">.</strong>
    <span className="js-random-interval glitched">Fielding</span>
    <strong className="js-random-interval glitched">.</strong>
    <style jsx>{`
      h1 {
        margin-top: 60px;
        font-weight: 900;
        white-space: nowrap;
      }
      h1 strong {
        font-weight: 900;
        color: rgba(190, 36, 70, 1);
      }
    `}</style>
  </h1>
);

const Intro = () => (
  <p>
    Hello, I&#39;m Fielding Johnston, a design-minded full stack software
    engineer working in the industry since 2005. Follow me on{' '}
    <a href="https://github.com/fielding/" className="glitched">
      github
    </a>,{' '}
    <a href="https://git.imbue.studio/fielding" className="glitched">
      git.imbue.studio
    </a>, and{' '}
    <a href="https://codepen.io/fielding" className="glitched">
      codepen
    </a>{' '}
    to see my latest work and inspiration. Check out my{' '}
    <a className="glitched" href="https://justfielding.com/resume.pdf">
      resume
    </a>{' '}
    and connect with me on{' '}
    <a href="https://linkedin.com/in/fieldingjohnston" className="glitched">
      linkedin
    </a>{' '}
    if you want to get to know me better.
  </p>
);

const Availability = () => (
  <p>
    I am available for hire for both freelance and full time positions, and
    willing to relocate for the right opportunity. Feel free to get in touch
    with me at{' '}
    <a href="mailto:hello@justfielding.com" className="glitched">
      hello@justfielding.com
    </a>.
  </p>
);

class GlitchEffect extends React.Component {
  componentDidMount() {
    glitchInit();
  }

  render() {
    return (
      <div>
        <svg
          xmlns="http://www.w3.org/2000/svg"
          version="1.1"
          className="svg-filters"
        >
          <defs>
            <filter id="filter-glitch">
              <feTurbulence
                type="fractalNoise"
                baseFrequency="0.00001"
                numOctaves="1"
                result="warp"
              />
              <feOffset dx="-90" dy="-90" result="warpOffset" />
              <feDisplacementMap
                xChannelSelector="R"
                yChannelSelector="G"
                scale="30"
                in="SourceGraphic"
                in2="warpOffset"
              />
            </filter>
          </defs>
        </svg>
        <style jsx global>{`
          .svg-filters {
            position: absolute;
            visibility: hidden;
            width: 1px;
            height: 1px;
          }
          .glitched {
            -webkit-transition: background-color 0.1s ease-out;
            -moz-transition: background-color 0.1s ease-out;
            transition: background-color 0.1s ease-out;
            transform: translateZ(0);
            outline: 90px solid transparent !important;
          }
          .glitched:focus {
            outline: none;
          }
        `}</style>
      </div>
    );
  }
}


export default Index;
