import React from 'react';
import ReactDOM from 'react-dom';

const data = [
	{
	  title: 'Banking',
	  content: <p>Make up some banking content</p>
	},
	{
	  title: 'Warehouse',
	  content: <p>Make up some warehouse content</p>
	}
  ];

  class Application extends React.Component {

	constructor(props) {
	  super(props);
	  this.state = {
		index: 0
	  }
	}

	render() {
	  return (
		<div className="main">
		  <div className="card">
			<h4>Tabs Example</h4>
			<div className="tab">
			  <ul className="tab-list">
				{
				  data.map( (tab, i) =>
					<li key={i}
					  data-active={ this.state.index === i }
					  onClick={ () => this.setState({ index: i }) }>
						{tab.title}
					</li>
				  )
				}
			  </ul>

			  <div className="tab-content">
				<div data-content={this.state.index + 1}>
				  {data[this.state.index].content}
				</div>
			  </div>
			</div>
		  </div>
		</div>
	  );
	}
  }

const App = () => <>
    <div className='page'>
    <div className='container'>
	<Application tabs={data} />
	</div>
	</div>
</>;

export default App;
