import React, { Component } from 'react';
import { render } from 'react-dom';
import { TweenMax } from 'gsap';
import "../styles/pages/works.scss";
import mobiledevelopment from "../assets/mobiledevelopment.svg";


const ELEMENTS = [
  { name: [
    <img src={mobiledevelopment} />    ]},
  { name: '2'},
  { name: '3'},
  { name: '4'},
  { name: '5'},
  { name: '6'},
  { name: '7'},
  { name: '8'},
  { name: '9'},
  { name: '10'},
]
// function Works() {
  class Works extends Component {
    constructor() {
    super();
    this.state = {
    };
  }

  transitionToSmall = () => {
    // Identify where the element starts 
    const largeElements = document.querySelectorAll('.largeElement');
    const smallElements = document.querySelectorAll('.smallElement');
    let xDifs = [];
    let yDifs = [];
    let scaleDifs = [];

    // Calculate the transform values 
    largeElements.forEach((el1, index) => {
    const transformVals = this.returnXYSDifference(el1, smallElements[index]);
    xDifs.push([transformVals[0]]);
    yDifs.push([transformVals[1]]);
    scaleDifs.push([transformVals[2]]);
    });

    // Animate using GSAP 
    TweenMax.staggerTo('.largeElement', 1, { 
      cycle: {
          x: function(index){
          return xDifs[index]      
          },
          y: function(index){
            return yDifs[index]      
          },
          scale: function(index){
            return scaleDifs[index]      
          }
      }
    }, 0.1, () => {
      // Switch the transformed element
      TweenMax.set('.smallElement', { autoAlpha: 1});
      TweenMax.set('.largeElement', { autoAlpha: 0, x: 0, y: 0, scale: 1 })
    })
  }

  transitionToLarge = () => {
     // Identify where the element starts 
     const largeElements = document.querySelectorAll('.largeElement');
     const smallElements = document.querySelectorAll('.smallElement');
     let xDifs = [];
     let yDifs = [];
     let scaleDifs = [];

     // Calculate the transform values 
     smallElements.forEach((el1, index) => {
     const transformVals = this.returnXYSDifference(el1, largeElements[index]);
     xDifs.push([transformVals[0]]);
     yDifs.push([transformVals[1]]);
     scaleDifs.push([transformVals[2]]);
     });

     // Animate using GSAP 
     TweenMax.staggerTo('.smallElement', 1, { 
       cycle: {
           x: function(index){
           return xDifs[index]      
           },
           y: function(index){
             return yDifs[index]      
           },
           scale: function(index){
             return scaleDifs[index]      
           }
       }
     }, 0.1, () => {
       // Switch the transformed element
       TweenMax.set('.largeElement', { autoAlpha: 1});
       TweenMax.set('.smallElement', { autoAlpha: 0, x: 0, y: 0, scale: 1 })
     })
  }

returnXYSDifference = (el1, el2) => {
  const rect1 = el1.getBoundingClientRect();
  const rect2 = el2.getBoundingClientRect();

  const xDif = rect2.left - rect1.left;
  const yDif = rect2.top - rect1.top;
  const scaleRatio = rect2.width / rect1.width;

  return [xDif, yDif, scaleRatio];
}

returnElements = (isSmall) => {
  return ELEMENTS.map(element => {
    return <div className={isSmall ? 'smallElement' : 'largeElement'} />
  })
}
render() {
    return (
      <main>

      <div className="largeElements" onClick={this.transitionToSmall}>
      {this.returnElements(false)}
      </div>

      <div className="smallElements" onClick={this.transitionToLarge}>
      {this.returnElements(true)}
      </div>

      </main>
    );
}
}

export default Works;

