// App.js
import React from 'react';
import "../styles/pages/careers.scss";
import nodejs from "../assets/node-js.svg";
import reactjs from "../assets/react.svg";
import angularjs from "../assets/angular.svg";
import python from "../assets/python.svg";
import ai from "../assets/ai.svg";
import mongodb from "../assets/mongo_db.svg";
import postgresql from "../assets/postgre_sql.svg";
import wordpress from "../assets/wordpress.svg";
import magento from "../assets/magento.svg";
import opencart from "../assets/opencart.svg";
import net from "../assets/net.svg";
import php from "../assets/php.svg";

const timelineData = [
    {
		text: [
			<img src={nodejs} />, ' NodeJs,', <br></br> , 
			<img src={reactjs} />,' ReactJs,', <br></br> , 
			<img src={angularjs} />,' AngularJs' , <br></br> , 
			<img src={net} />, ' .Net,', <br></br> ,
			<img src={php} />, ' Php,', <br></br> , 
			<img src={python} />, ' Python' , <br></br> , 
			<img src={wordpress} />, ' WordPress,', <br></br> , 
			<img src={magento} />, ' Magento,', <br></br> , 
			<img src={opencart} />, ' Opencart' , <br></br> , 
			<img src={ai} />, 'Ai,', <br></br> , 
			<img src={mongodb} />, 'MongoDB,', <br></br> , 
			<img src={postgresql} />, ' PostgreSql' ,
			  ],
        category: {
			tag: 'Server Technologies',
			color: '#FFDB14'
		}
    },
    {
		text: [
			<img src={nodejs} />, ' NodeJs,', <br></br> , 
			<img src={reactjs} />,' ReactJs,', <br></br> , 
			<img src={angularjs} />,' AngularJs' , <br></br> , 
			<img src={net} />, ' .Net,', <br></br> ,
			<img src={php} />, ' Php,', <br></br> , 
			<img src={python} />, ' Python' , <br></br> , 
			<img src={wordpress} />, ' WordPress,', <br></br> , 
			<img src={magento} />, ' Magento,', <br></br> , 
			<img src={opencart} />, ' Opencart' , <br></br> , 
			<img src={ai} />, 'Ai,', <br></br> , 
			<img src={mongodb} />, 'MongoDB,', <br></br> , 
			<img src={postgresql} />, ' PostgreSql' ,
			  ],
        category: {
			tag: 'Frontend Technologies',
			color: '#e17b77'
		}
    },
    {
        text: 'Got 1.000 followers on Twitter',
        category: {
			tag: 'twitter',
			color: '#1DA1F2'
		}
    },
    {
        text:
            'I published my first article in the FreeCodeCamp Medium Publication',
        category: {
			tag: 'medium',
			color: '#018f69'
		}
    }
]

const TimelineItem = ({ data }) => (
    <div className="timeline-item">
        <div className="timeline-item-content">
            <span className="tag" style={{ background: data.category.color }}>
                {data.category.tag}
            </span>
			<time>{data.date}</time>
            <p>{data.text}</p>
            <span className="circle" />
        </div>
    </div>
);

const Timeline = () =>
    timelineData.length > 0 && (
        <div className="timeline-container ">
            {timelineData.map((data, idx) => (
                <TimelineItem data={data} key={idx} />
            ))}
        </div>
    );

const App = () => <>
	<Timeline />
</>;

export default App;
